#include "Arduino.h"#include "accelerator.h"#include <elapsedMillis.h>#include "sending.h"#include "four_sensors.h"int pollingPerMinute = 4;int loopDelay = 60000/pollingPerMinute;elapsedMillis timeElapsed;void setup() {  Serial.begin(9600);  while(!Serial);  Serial.println("hello");  sensors_setup();  accelerator_setup();  sending_setup();  Serial.println("Sensors set up");}void loop() {  //Serial.print("Ultrasound: ");  //Serial.println(getUltrasound());  if(timeElapsed > loopDelay){    Serial.print("Acc: ");    int acc = accelerator_poll();    Serial.println(acc);    int ultr = getUltrasound();    Serial.print("Ultrasound: ");    Serial.println(ultr);    float ditemp = getDiTemp();    Serial.print("Digital Temp: ");    Serial.println(ditemp);     float antemp = getAnTemp();    Serial.print("Analog Temp: ");    Serial.println(antemp);    int pir = getPIR();    Serial.print("PIR: ");    Serial.println(pir);    timeElapsed = 0;        String data = String(acc)+" "+String(ultr)+" "+String(ditemp)+" "+String(antemp)+" "+String(pir);    Serial.println(data);    sendData(data);  }  loopUltrasound();  loopPIR();  accelerator_loop();  delay(50);        }